import React from 'react';
import Select from '@material-ui/core/Select';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import Box from '@material-ui/core/Box';
import axios from 'axios';

const API_ROUTE = "http://localhost:5000"

class GolferService extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            supColumns: null,
            supColItems: null
        }
        this.getSupColumns = this.getSupColumns.bind(this)
    }

    getSupColumns() {
        return axios.get(API_ROUTE + "/supcolumns")
            .then(response => {
                console.log(response.data.supcolumns)
                this.setState({
                    supColumns: response.data.supcolumns
                })
            })
    }

    fillSupColumns() {
        let cols = this.state.supcolumns;
        let menuItems = cols.map((column) =>
            <MenuItem value={column.id} key={column.name}>{column.name}</MenuItem>
        );
        this.setState({
            supColItems: menuItems
        })
    }

    async componentDidMount() {
        await this.getSupColumns()
        this.fillSupColumns()
    }

    render() {
        return (
            <div class="graph">
                <h4>
                    Do supervised ML on a golfer!
                </h4>
                <InputLabel>Attribute to predict</InputLabel>
                <Select
                    onChange={this.setMap}>
                    {this.state.mapItems}
                </Select>
                <InputLabel>Bins</InputLabel>
                <Select
                    onChange={this.setMap}>
                    {this.state.mapItems}
                </Select>
                <div class="file-field input-field">
                    <Box display="flex" alignSelf="flex-end" justifyContent="center" className="upl">
                        <a class="waves-effect waves-light btn" onClick={this.sendGraphInfo}>Display Graph</a>
                    </Box>
                    <Box display="flex" justifyContent="center">
                        <img src={this.state.disp_image} onChange={this.changeDispImage} id="graph-upload" className="ImageSize" />
                    </Box>
                </div>
            </div>
        );
    }




}


export default GolferService;
